'use strict';

var entryFactory = require('../../../../factory/EntryFactory');

/**
 * Create an entry to modify the name of an an element.
 *
 * @param  {djs.model.Base} element
 * @param  {Object} options
 * @param  {string} options.id the id of the entry
 * @param  {string} options.label the label of the entry
 *
 * @return {Array<Object>} return an array containing
 *                         the entry to modify the name
 */
module.exports = function(element, options, translate) {

  options = options || {};
  var id = options.id || 'rule',
      label = options.label || translate('Rule'),
      modelProperty = options.modelProperty || 'rule',
      inputArr = options.inputArr || [];

  var nameEntry = entryFactory.selectBox({
    id: id,
    label: label,
    modelProperty: modelProperty,
    selectOptions: inputArr,
    get: options.get,
    set: options.set
  });

  return [ nameEntry ];

};
