'use strict';

var entryFactory = require('bpmn-js-properties-panel/lib/factory/EntryFactory');

/**
 * Create an entry to modify the property of an an element.
 *
 * @param  {djs.model.Base} element
 * @param  {Object} options
 * @param  {string} options.id the id of the entry
 * @param  {string} options.label the label of the entry
 *
 * @return {Array<Object>} return an array containing
 *                         the entry to modify the name
 */
module.exports = function(element, options, translate) {

  options = options || {};
  var id = options.id || 'label',
      label = options.label || translate('Label');

  var nameEntry = entryFactory.label({
    id: id,
    labelText: label,
    set: options.set
  });

  return [ nameEntry ];

};
